// src/pages/AdminDashboard.js
import React, { useEffect, useState } from "react";
import {
  Box,
  Tabs,
  Tab,
  Typography,
  Card,
  CardContent,
  TextField,
  Button,
  Stack,
  IconButton,
  Avatar,
  Divider,
} from "@mui/material";
import axios from "axios";
import { motion } from "framer-motion";
import "../styles/Admin.css";


const AdminDashboard = () => {
  const [tab, setTab] = useState(0);
  const [loading, setLoading] = useState(false);

  // Data state (fetched from backend)
  const [announcements, setAnnouncements] = useState([]);
  const [newAnnouncement, setNewAnnouncement] = useState("");

  const [rulebooks, setRulebooks] = useState({
    symposium: "",
    quiz: "",
    hackathon: "",
    model: "",
  });
  const [rulebookFiles, setRulebookFiles] = useState({
    symposium: null,
    quiz: null,
    hackathon: null,
    model: null,
  });

  const [socialLinks, setSocialLinks] = useState({
    instagram: "",
    youtube: "",
    linkedin: "",
    twitter: "",
  });

  const [contact, setContact] = useState({ email: "", phone: "" });
  const [clubInfo, setClubInfo] = useState({ name: "", logoUrl: "" });
  const [logoFile, setLogoFile] = useState(null);

  // on mount -> fetch current admin data
  useEffect(() => {
    fetchAdminData();
  }, []);

  const fetchAdminData = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/admin");
      const data = res.data || {};
      setAnnouncements(data.announcements || []);
      setRulebooks(data.rulebooks || {});
      setSocialLinks(data.socialLinks || {});
      setContact(data.contact || {});
      setClubInfo(data.clubInfo || {});
    } catch (err) {
      console.error("Failed to fetch admin data:", err);
      alert("Failed to fetch admin data. Check backend.");
    }
  };

  /* ---------- ANNOUNCEMENTS ---------- */
  const addAnnouncementLocally = () => {
    if (!newAnnouncement.trim()) return;
    setAnnouncements((p) => [...p, newAnnouncement.trim()]);
    setNewAnnouncement("");
  };

  const removeAnnouncement = (i) => {
    setAnnouncements((p) => p.filter((_, idx) => idx !== i));
  };

  const saveAnnouncements = async () => {
    setLoading(true);
    try {
      await axios.put("http://localhost:5000/api/admin/announcements", {
        announcements,
      });
      alert("Announcements saved.");
    } catch (err) {
      console.error(err);
      alert("Failed to save announcements.");
    } finally {
      setLoading(false);
    }
  };

  /* ---------- SOCIAL LINKS & CLUB INFO ---------- */
  const saveSocialLinks = async () => {
    setLoading(true);
    try {
      await axios.put("http://localhost:5000/api/admin/social-links", {
        socialLinks,
      });
      alert("Social links saved.");
    } catch (err) {
      console.error(err);
      alert("Failed to save social links.");
    } finally {
      setLoading(false);
    }
  };

  const saveClubInfo = async () => {
    setLoading(true);
    try {
      // If logo file provided, upload it first
      let uploadedLogoUrl = clubInfo.logoUrl || "";
      if (logoFile) {
        const form = new FormData();
        form.append("file", logoFile);
        form.append("field", "clubLogo"); // optional marker
        const up = await axios.post("http://localhost:5000/api/admin/upload", form, {
          headers: { "Content-Type": "multipart/form-data" },
        });
        uploadedLogoUrl = up.data.fileUrl; // backend returns fileUrl
      }

      await axios.put("http://localhost:5000/api/admin/club-info", {
        clubInfo: { ...clubInfo, logoUrl: uploadedLogoUrl },
      });
      alert("Club info saved.");
      fetchAdminData();
      setLogoFile(null);
    } catch (err) {
      console.error(err);
      alert("Failed to save club info.");
    } finally {
      setLoading(false);
    }
  };

  /* ---------- CONTACT ---------- */
  const saveContact = async () => {
    setLoading(true);
    try {
      await axios.put("http://localhost:5000/api/admin/contact", {
        contact,
      });
      alert("Contact info saved.");
    } catch (err) {
      console.error(err);
      alert("Failed to save contact info.");
    } finally {
      setLoading(false);
    }
  };

  /* ---------- RULEBOOKS (file upload) ---------- */
  const handleRulebookFileChange = (e, key) => {
    setRulebookFiles((p) => ({ ...p, [key]: e.target.files[0] || null }));
  };

  const uploadSingleRulebook = async (key) => {
    const file = rulebookFiles[key];
    if (!file) return alert("Choose a file first.");
    setLoading(true);
    try {
      const form = new FormData();
      form.append("file", file);
      form.append("type", key); // e.g. symposium/quiz/hackathon/model
      const res = await axios.post("http://localhost:5000/api/admin/upload", form, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      // Response should contain fileUrl
      const fileUrl = res.data.fileUrl;
      // Save to DB rulebooks object
      const updated = { ...rulebooks, [key]: fileUrl };
      await axios.put("http://localhost:5000/api/admin/rulebooks", { rulebooks: updated });
      setRulebooks(updated);
      alert(`${key} uploaded and saved.`);
      // clear input
      setRulebookFiles((p) => ({ ...p, [key]: null }));
      fetchAdminData();
    } catch (err) {
      console.error(err);
      alert("Upload failed.");
    } finally {
      setLoading(false);
    }
  };

  const handleTabChange = (e, newVal) => setTab(newVal);

  return (
    <div className="admin-container">
      <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
        <Card className="admin-card">
          <CardContent>
            <Typography variant="h4" gutterBottom>
              üßë‚Äçüíº Admin Dashboard
            </Typography>

            <Tabs value={tab} onChange={handleTabChange} centered>
              <Tab label="Announcements" />
              <Tab label="Rulebooks" />
              <Tab label="Social & Club" />
              <Tab label="Contact" />
            </Tabs>

            <Divider sx={{ my: 2 }} />

            {/* ---------------- Announcements Tab ---------------- */}
            {tab === 0 && (
              <Box>
                <Typography variant="h6">Manage Announcements</Typography>
                <Stack direction="row" spacing={2} sx={{ mt: 2 }}>
                  <TextField
                    label="Add Announcement"
                    fullWidth
                    value={newAnnouncement}
                    onChange={(e) => setNewAnnouncement(e.target.value)}
                    onKeyDown={(e) => e.key === "Enter" && addAnnouncementLocally()}
                  />
                  <Button variant="contained" onClick={addAnnouncementLocally}>
                    Add
                  </Button>
                </Stack>

                <Box sx={{ mt: 2 }}>
                  {announcements.length === 0 ? (
                    <Typography>No announcements yet.</Typography>
                  ) : (
                    announcements.map((a, idx) => (
                      <Card key={idx} sx={{ mb: 1 }}>
                        <CardContent sx={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                          <Typography>{a}</Typography>
                          <Button color="error" onClick={() => removeAnnouncement(idx)}>
                            Remove
                          </Button>
                        </CardContent>
                      </Card>
                    ))
                  )}
                </Box>

                <Stack direction="row" spacing={2} sx={{ mt: 2 }}>
                  <Button variant="contained" onClick={saveAnnouncements} disabled={loading}>
                    Save Announcements
                  </Button>
                  <Button variant="outlined" onClick={fetchAdminData}>Reload</Button>
                </Stack>
              </Box>
            )}

            {/* ---------------- Rulebooks Tab ---------------- */}
            {tab === 1 && (
              <Box>
                <Typography variant="h6">Manage Rulebooks (upload PDF)</Typography>
                <Stack spacing={2} sx={{ mt: 2 }}>
                  {["symposium", "quiz", "hackathon", "model"].map((key) => (
                    <Card key={key} sx={{ p: 2 }}>
                      <Typography sx={{ textTransform: "capitalize", fontWeight: 600 }}>{key}</Typography>
                      <Typography variant="body2" sx={{ mb: 1 }}>
                        Current: {rulebooks[key] ? <a href={rulebooks[key]} target="_blank" rel="noreferrer">open</a> : "none"}
                      </Typography>

                      <Stack direction="row" spacing={2} alignItems="center">
                        <input
                          id={`file-${key}`}
                          accept="application/pdf"
                          type="file"
                          style={{ display: "none" }}
                          onChange={(e) => handleRulebookFileChange(e, key)}
                        />
                        <label htmlFor={`file-${key}`}>
                          <Button variant="contained" component="span">Choose PDF</Button>
                        </label>
                        <Typography>{rulebookFiles[key] ? rulebookFiles[key].name : "No file chosen"}</Typography>
                        <Button variant="outlined" onClick={() => uploadSingleRulebook(key)} disabled={!rulebookFiles[key]}>
                          Upload & Save
                        </Button>
                      </Stack>
                    </Card>
                  ))}
                </Stack>
              </Box>
            )}

            {/* ---------------- Social & Club Tab ---------------- */}
            {tab === 2 && (
              <Box>
                <Typography variant="h6">Social Links</Typography>
                <Stack spacing={2} sx={{ mt: 2, width: { xs: "100%", sm: "80%" } }}>
                  <TextField label="Instagram URL" value={socialLinks.instagram} onChange={(e) => setSocialLinks({...socialLinks, instagram: e.target.value})} />
                  <TextField label="YouTube URL" value={socialLinks.youtube} onChange={(e) => setSocialLinks({...socialLinks, youtube: e.target.value})} />
                  <TextField label="LinkedIn URL" value={socialLinks.linkedin} onChange={(e) => setSocialLinks({...socialLinks, linkedin: e.target.value})} />
                  <TextField label="Twitter URL" value={socialLinks.twitter} onChange={(e) => setSocialLinks({...socialLinks, twitter: e.target.value})} />

                  <Button variant="contained" onClick={saveSocialLinks}>
                    Save Social Links
                  </Button>
                </Stack>

                <Divider sx={{ my: 3 }} />

                <Typography variant="h6">Club Info & Logo</Typography>
                <Stack spacing={2} sx={{ mt: 2, width: { xs: "100%", sm: "80%" } }}>
                  <TextField label="Club Name" value={clubInfo.name} onChange={(e) => setClubInfo({...clubInfo, name: e.target.value})} />
                  <TextField label="Logo URL (optional)" value={clubInfo.logoUrl} onChange={(e) => setClubInfo({...clubInfo, logoUrl: e.target.value})} />
                  <Box>
                    <input type="file" accept="image/*" id="logo-file" style={{ display: "none" }} onChange={(e) => setLogoFile(e.target.files[0])} />
                    <label htmlFor="logo-file">
                      <Button component="span" variant="contained">Upload Logo</Button>
                    </label>
                    <Typography sx={{ mt: 1 }}>{logoFile ? logoFile.name : "No file chosen"}</Typography>
                  </Box>
                  <Stack direction="row" spacing={2}>
                    <Button variant="contained" onClick={saveClubInfo}>Save Club Info</Button>
                    <Button variant="outlined" onClick={() => { setClubInfo({ name: "", logoUrl: ""}); setLogoFile(null); }}>Reset</Button>
                  </Stack>
                </Stack>
              </Box>
            )}

            {/* ---------------- Contact Tab ---------------- */}
            {tab === 3 && (
              <Box>
                <Typography variant="h6">Contact Info</Typography>
                <Stack spacing={2} sx={{ mt: 2, width: { xs: "100%", sm: "80%" } }}>
                  <TextField label="Contact Email" value={contact.email} onChange={(e) => setContact({...contact, email: e.target.value})} />
                  <TextField label="Contact Phone" value={contact.phone} onChange={(e) => setContact({...contact, phone: e.target.value})} />
                  <Stack direction="row" spacing={2}>
                    <Button variant="contained" onClick={saveContact}>Save Contact</Button>
                    <Button variant="outlined" onClick={fetchAdminData}>Reload</Button>
                  </Stack>
                </Stack>
              </Box>
            )}
          </CardContent>
        </Card>
      </motion.div>
    </div>
  );
};

export default AdminDashboard;
