import React, { useEffect, useState } from "react";
import { Button, Typography, TextField, Card, CardContent } from "@mui/material";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";
import "./styles/Admin.css";

const AdminDashboard = () => {
  const [newsList, setNewsList] = useState([]);
  const [newItem, setNewItem] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const isAdmin = sessionStorage.getItem("isAdmin");
    if (!isAdmin) {
      navigate("/admin");
    }
  }, [navigate]);

  const addNews = () => {
    if (newItem.trim()) {
      setNewsList([...newsList, newItem]);
      setNewItem("");
    }
  };

  const logout = () => {
    sessionStorage.removeItem("isAdmin");
    navigate("/");
  };

  return (
    <div className="admin-container">
      <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
        <Typography variant="h4" gutterBottom>
          üßë‚Äçüíº Admin Dashboard
        </Typography>

        <Button
          variant="contained"
          color="secondary"
          sx={{ mb: 3 }}
          onClick={logout}
        >
          Logout
        </Button>

        <Card className="admin-card">
          <CardContent>
            <Typography variant="h6">Add New Announcement</Typography>
            <TextField
              label="Enter announcement"
              fullWidth
              sx={{ mt: 2 }}
              value={newItem}
              onChange={(e) => setNewItem(e.target.value)}
            />
            <Button
              variant="contained"
              color="primary"
              sx={{ mt: 2 }}
              onClick={addNews}
            >
              Add
            </Button>
          </CardContent>
        </Card>

        <Card className="admin-card">
          <CardContent>
            <Typography variant="h6" gutterBottom>
              üì¢ Current Announcements
            </Typography>
            {newsList.length === 0 ? (
              <Typography>No announcements yet.</Typography>
            ) : (
              <ul>
                {newsList.map((news, index) => (
                  <li key={index}>{news}</li>
                ))}
              </ul>
            )}
          </CardContent>
        </Card>
      </motion.div>
    </div>
  );
};

export default AdminDashboard;
