// src/pages/AdminDashboard.js
import React, { useState, useEffect } from "react";
import {
  Box,
  Typography,
  Tabs,
  Tab,
  TextField,
  Button,
  Stack,
  CircularProgress,
  IconButton,
} from "@mui/material";
import { Delete as DeleteIcon } from "@mui/icons-material";
import axios from "axios";

const AdminDashboard = () => {
  const [tab, setTab] = useState(0);
  const [loading, setLoading] = useState(false);
  const [adminData, setAdminData] = useState({});
  const [announcements, setAnnouncements] = useState([]);
  const [rulebooks, setRulebooks] = useState({});
  const [registrationLink, setRegistrationLink] = useState("");
  const [socialLinks, setSocialLinks] = useState({});
  const [contact, setContact] = useState({
    main: { name: "", title: "", number: "" },
    coordinators: [],
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    setLoading(true);
    try {
      const res = await axios.get("http://localhost:5000/api/admin");
      const data = res.data || {};
      setAdminData(data);
      setAnnouncements(data.announcements || []);
      setRulebooks(data.rulebooks || {});
      setRegistrationLink(data.registration?.googleFormLink || "");
      setSocialLinks(data.socialLinks || {});
      setContact(
        data.contact || { main: { name: "", title: "", number: "" }, coordinators: [] }
      );
    } catch (err) {
      console.error("Fetch error:", err);
    } finally {
      setLoading(false);
    }
  };

  const saveAnnouncements = async () => {
    setLoading(true);
    try {
      await axios.put("http://localhost:5000/api/admin/announcements", {
        announcements,
      });
      alert("‚úÖ Announcements updated!");
    } catch (err) {
      console.error(err);
      alert("Failed to save announcements.");
    } finally {
      setLoading(false);
    }
  };

  const saveRulebooks = async () => {
    setLoading(true);
    try {
      await axios.put("http://localhost:5000/api/admin/rulebooks", {
        rulebooks,
      });
      alert("‚úÖ Rulebooks updated!");
    } catch (err) {
      console.error(err);
      alert("Failed to save rulebooks.");
    } finally {
      setLoading(false);
    }
  };

  const saveRegistration = async () => {
    setLoading(true);
    try {
      await axios.put("http://localhost:5000/api/admin/registration", {
        registration: { googleFormLink: registrationLink },
      });
      alert("‚úÖ Registration link updated!");
    } catch (err) {
      console.error(err);
      alert("Failed to save registration link.");
    } finally {
      setLoading(false);
    }
  };

  const saveSocialLinks = async () => {
    setLoading(true);
    try {
      await axios.put("http://localhost:5000/api/admin/social-links", {
        socialLinks,
      });
      alert("‚úÖ Social links updated!");
    } catch (err) {
      console.error(err);
      alert("Failed to save social links.");
    } finally {
      setLoading(false);
    }
  };

  const saveContact = async () => {
    setLoading(true);
    try {
      await axios.put("http://localhost:5000/api/admin/contact", { contact });
      alert("‚úÖ Contact info updated!");
    } catch (err) {
      console.error(err);
      alert("Failed to save contact info.");
    } finally {
      setLoading(false);
    }
  };

  // üóëÔ∏è Delete specific coordinator
  const deleteCoordinator = async (index) => {
    if (!window.confirm("Delete this coordinator?")) return;
    try {
      const newList = contact.coordinators.filter((_, i) => i !== index);
      const updatedContact = { ...contact, coordinators: newList };
      setContact(updatedContact);
      await axios.put("http://localhost:5000/api/admin/contact", { contact: updatedContact });
      alert("‚ùå Coordinator deleted successfully!");
    } catch (err) {
      console.error(err);
      alert("Failed to delete coordinator.");
    }
  };

  // üóëÔ∏è Delete main contact only
  const deleteMainContact = async () => {
    if (!window.confirm("Delete main contact info?")) return;
    try {
      const updatedContact = { ...contact, main: { name: "", title: "", number: "" } };
      setContact(updatedContact);
      await axios.put("http://localhost:5000/api/admin/contact", { contact: updatedContact });
      alert("‚ùå Main contact deleted successfully!");
    } catch (err) {
      console.error(err);
      alert("Failed to delete main contact.");
    }
  };

  return (
    <Box sx={{ p: 4 }}>
      <Typography variant="h4" sx={{ mb: 3, fontWeight: "bold", color: "#d32f2f" }}>
        Admin Dashboard
      </Typography>

      <Tabs value={tab} onChange={(e, val) => setTab(val)}>
        <Tab label="Announcements" />
        <Tab label="Rulebooks" />
        <Tab label="Registration" />
        <Tab label="Social Links" />
        <Tab label="Contact Info" />
      </Tabs>

      {loading && <CircularProgress sx={{ mt: 3 }} />}

      {/* Announcements */}
      {tab === 0 && (
        <Stack spacing={2} sx={{ mt: 3 }}>
          <Typography variant="h6">Announcements</Typography>
          {announcements.map((note, i) => (
            <TextField
              key={i}
              fullWidth
              label={`Announcement ${i + 1}`}
              value={note}
              onChange={(e) => {
                const newNotes = [...announcements];
                newNotes[i] = e.target.value;
                setAnnouncements(newNotes);
              }}
            />
          ))}
          <Button onClick={() => setAnnouncements([...announcements, ""])}>+ Add</Button>
          <Button variant="contained" onClick={saveAnnouncements}>
            Save
          </Button>
        </Stack>
      )}

      {/* Rulebooks */}
      {tab === 1 && (
        <Stack spacing={2} sx={{ mt: 3 }}>
          <Typography variant="h6">Rulebook Links (URLs)</Typography>
          {["symposium", "quiz", "hackathon", "model"].map((key) => (
            <TextField
              key={key}
              fullWidth
              label={`${key.charAt(0).toUpperCase() + key.slice(1)} Rulebook URL`}
              value={rulebooks[key] || ""}
              onChange={(e) =>
                setRulebooks({ ...rulebooks, [key]: e.target.value })
              }
            />
          ))}
          <Button variant="contained" onClick={saveRulebooks}>
            Save Rulebooks
          </Button>
        </Stack>
      )}

      {/* Registration */}
      {tab === 2 && (
        <Stack spacing={2} sx={{ mt: 3 }}>
          <Typography variant="h6">Google Form Registration Link</Typography>
          <TextField
            label="Google Form Link"
            fullWidth
            value={registrationLink}
            onChange={(e) => setRegistrationLink(e.target.value)}
          />
          <Button variant="contained" onClick={saveRegistration}>
            Save Link
          </Button>
        </Stack>
      )}

      {/* Social Links */}
      {tab === 3 && (
        <Stack spacing={2} sx={{ mt: 3 }}>
          <Typography variant="h6">Social Media Links</Typography>
          {["instagram", "youtube", "linkedin", "twitter"].map((key) => (
            <TextField
              key={key}
              label={`${key.charAt(0).toUpperCase() + key.slice(1)} URL`}
              fullWidth
              value={socialLinks[key] || ""}
              onChange={(e) =>
                setSocialLinks({ ...socialLinks, [key]: e.target.value })
              }
            />
          ))}
          <Button variant="contained" onClick={saveSocialLinks}>
            Save Links
          </Button>
        </Stack>
      )}

      {/* ‚úÖ Updated Contact Info with Individual Delete */}
      {tab === 4 && (
        <Stack spacing={3} sx={{ mt: 3 }}>
          <Typography variant="h6" sx={{ fontWeight: "bold" }}>
            Main Contact
          </Typography>

          <Stack direction="row" spacing={2} alignItems="center">
            <TextField
              label="Name"
              fullWidth
              value={contact.main?.name || ""}
              onChange={(e) =>
                setContact({
                  ...contact,
                  main: { ...contact.main, name: e.target.value },
                })
              }
            />
            <TextField
              label="Title"
              fullWidth
              value={contact.main?.title || ""}
              onChange={(e) =>
                setContact({
                  ...contact,
                  main: { ...contact.main, title: e.target.value },
                })
              }
            />
            <TextField
              label="Number"
              fullWidth
              value={contact.main?.number || ""}
              onChange={(e) =>
                setContact({
                  ...contact,
                  main: { ...contact.main, number: e.target.value },
                })
              }
            />
            <IconButton color="error" onClick={deleteMainContact}>
              <DeleteIcon />
            </IconButton>
          </Stack>

          <Button variant="contained" color="primary" onClick={saveContact} sx={{ width: "200px" }}>
            Save Main Contact
          </Button>

          <Typography variant="h6" sx={{ fontWeight: "bold", mt: 3 }}>
            Coordinators
          </Typography>

          {contact.coordinators?.map((coord, i) => (
            <Stack direction="row" spacing={2} key={i} alignItems="center">
              <TextField
                label="Name"
                fullWidth
                value={coord.name}
                onChange={(e) => {
                  const newList = [...contact.coordinators];
                  newList[i].name = e.target.value;
                  setContact({ ...contact, coordinators: newList });
                }}
              />
              <TextField
                label="Title"
                fullWidth
                value={coord.title}
                onChange={(e) => {
                  const newList = [...contact.coordinators];
                  newList[i].title = e.target.value;
                  setContact({ ...contact, coordinators: newList });
                }}
              />
              <TextField
                label="Number"
                fullWidth
                value={coord.number}
                onChange={(e) => {
                  const newList = [...contact.coordinators];
                  newList[i].number = e.target.value;
                  setContact({ ...contact, coordinators: newList });
                }}
              />
              <IconButton color="error" onClick={() => deleteCoordinator(i)}>
                <DeleteIcon />
              </IconButton>
            </Stack>
          ))}

          <Stack direction="row" spacing={2}>
            <Button
              variant="outlined"
              onClick={() =>
                setContact({
                  ...contact,
                  coordinators: [
                    ...contact.coordinators,
                    { name: "", title: "", number: "" },
                  ],
                })
              }
            >
              + Add Coordinator
            </Button>
            <Button variant="contained" color="success" onClick={saveContact}>
              Save All Contacts
            </Button>
          </Stack>
        </Stack>
      )}
    </Box>
  );
};

export default AdminDashboard;
